import java.util.*;public class Bai03 {	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		String paragraph = "The Edge Surf is of course also a whole lot better, which will hopefully win Microsoft some converts. It offers time trial, support for other input methods like touch and gamepads, like accessibility improvements, high scores, and remastered visuals.";;				System.out.println("Paragraph co luong tu la: " + countWord(paragraph));		System.out.println("Paragraph co so cau la: " + countSentences(paragraph));				System.out.println("Nhap tu muon dem su xuat hien trong paragraph: ");		String word = sc.nextLine();		System.out.println("Vay tu " + word + " xuat hien " + countAppear(paragraph, word) + " lan");	}		public static int countWord(String paragraph) {		String formatedStr = paragraph.replaceAll("[,.!]", "");		String[] words = formatedStr.split("\\s++");		return words.length;	}		public static int countSentences(String paragraph) {		int count = 0;				char[] s1 = paragraph.toCharArray(); 		for (int i = 0; i < s1.length; i++) {			if(s1[i] == '.') count++;		}		return count;	}		public static int countAppear(String paragraph, String word) {		int count = 0;				String formatedStr = paragraph.replaceAll("[,.!]", "");		formatedStr = paragraph.toLowerCase();		word = word.toLowerCase();				String[] words = formatedStr.split("\\s++");				for(String part : words) {			if(part.equals(word))				count++;		}		return count;  		}	}