public class RegularPolygon {	private String name;	private int egdeAmount;	private int egdeLength;		public RegularPolygon() {		this.name = "";		this.egdeAmount = 3;		this.egdeLength = 1;	}		public RegularPolygon(String name, int egdeAmount, int egdeLength) {		this.name = name;    		this.egdeAmount = egdeAmount;    		this.egdeLength = egdeLength;	}		public RegularPolygon(String name, int egdeAmount) {		this.name = name;    		this.egdeAmount = egdeAmount;    		this.egdeLength = 1;	}		public RegularPolygon(RegularPolygon polygon) {		this.name = polygon.name;    		this.egdeAmount = polygon.egdeAmount;    		this.egdeLength = polygon.egdeLength;	}		public String getName() {		return this.name;	}		public int getEgdeAmount() {		return this.egdeAmount;	}		public int getEgdeLength() {		return this.egdeLength;	}		public void setName(String name) {		this.name = name;	}		public void setEgdeAmount(int egdeAmount) {		this.egdeAmount = egdeAmount;	}		public void setEgdeLength(int egdeLength) {		this.egdeLength = egdeLength;	}		public String getPolygon() {		if(this.egdeAmount == 3) return "Triagle";		else if(this.egdeAmount == 4) return "Quadrangle";		else if(this.egdeAmount == 5) return "Pentagon";		else if(this.egdeAmount == 6) return "Hexagon";		else return "Polygon has the number of egdes greater than 6";			}		public double getPerimeter() {		return this.egdeLength * this.egdeAmount;	}		public double getArea() {		double a;		if(this.egdeAmount == 3)	a = 0.433;		else if(this.egdeAmount == 4)	a = 1;		else if(this.egdeAmount == 5)	a = 1.72;		else if(this.egdeAmount == 6)	a = 2.595;		else	return -1;		return Math.pow(this.egdeAmount, 2) * a;	}		public String toString() {		return this.name + " - " + this.getPolygon() + " - " + this.getArea();	}}